package swingPros;import javax.swing.*;/*** * showInputDialog(), showConfirmDialog() & showMessageDialog */public class LoopConfirmationDialog {    public static void main(String[] args) {        int sum = 0;        int option = 0;        while (option == JOptionPane.YES_OPTION){            String stringData = JOptionPane.showInputDialog("Enter an int value: ");            int data = Integer.parseInt(stringData);            sum += data;            option = JOptionPane.showConfirmDialog(null, "Continue?");        }        JOptionPane.showMessageDialog(null, "The sum: "+sum);    }}