package swingPros;import javax.swing.*;/*** * showConfirmDialog() */public class ShowConfirmationDialog {    public static void main(String[] args) {        int answer = JOptionPane.showConfirmDialog(                null,                "Bro, do you even code?",                "Yes/No/Cancel",                JOptionPane.YES_NO_CANCEL_OPTION);        switch (answer){            case 0 -> showMessage("User clicked Yes", "Yes");            case 1 -> showMessage("User clicked NO", "No");            case 2 -> showMessage("User clicked CANCEL", "Cancel");            case (-1) -> showMessage("User clicked EXIT", "Exit");        }    }    public static void showMessage(String message, String title){        JOptionPane.showMessageDialog(null,                message,                title,                JOptionPane.INFORMATION_MESSAGE);    }}