package swingPros;import javax.swing.*;public class ShowInputDialog {    public static void main(String[] args) {        String name = JOptionPane.showInputDialog(null,                "What is your name?",                "Name Please?",                JOptionPane.QUESTION_MESSAGE);        if(name != null){            JOptionPane.showMessageDialog(null,                    "Your name: "+name,                    "Information Message",                    JOptionPane.INFORMATION_MESSAGE);        }    }}