package swingPros;import javax.swing.*;/*** * ShowMessageDialog(Method overloading) */public class ShowMessageDialog {    public static void main(String[] args) {        JOptionPane.showMessageDialog(null,                "This is plain text",                "Plain Message",                JOptionPane.PLAIN_MESSAGE);        JOptionPane.showMessageDialog(null,                "This is a message ",                "Error Message",                JOptionPane.ERROR_MESSAGE);        JOptionPane.showMessageDialog(null,                "This is a information message ",                "Information Message",                JOptionPane.INFORMATION_MESSAGE);        JOptionPane.showMessageDialog(null,                "This is a question message ",                "Question Message",                JOptionPane.QUESTION_MESSAGE);        JOptionPane.showMessageDialog(null,                "This is a warning message ",                "Warning Message",                JOptionPane.WARNING_MESSAGE);    }}