package swingPros;import javax.swing.*;/*** * showOptionDialog(custom values) */public class ShowOptionDialog {    public static void main(String[] args) {        final String[] responses = {"No, You're awesome!", "Thank you!", "*Blush*"};        ImageIcon imageIcon = new ImageIcon();        int answer = JOptionPane.showOptionDialog(                null,                "You are awesome!",                "Simple Information",                JOptionPane.YES_NO_CANCEL_OPTION,                JOptionPane.PLAIN_MESSAGE,                imageIcon,                responses,                0                );        System.out.println(answer);    }}